import React, { useEffect, useState } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import axios from 'axios';

export default function App() {
  const [events, setEvents] = useState([]);
  const [missingEarnings, setMissingEarnings] = useState([]);
  const [dividends, setDividends] = useState([]);
  const [missingDividends, setMissingDividends] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/earnings').then(resp => {
      const { results, faltantes } = resp.data;
      const ev = results.map(item => ({
        title: item.ticker,
        date: item.date
      }));
      setEvents(ev);
      setMissingEarnings(faltantes);
    });

    axios.get('http://localhost:5000/api/dividends').then(resp => {
      const { results, faltantes } = resp.data;
      setDividends(results);
      setMissingDividends(faltantes);
    });
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Earnings & Dividends Calendar</h1>

      <FullCalendar
        plugins={[dayGridPlugin]}
        initialView="dayGridMonth"
        events={events}
        height="auto"
      />

      {missingEarnings.length > 0 && (
        <div className="mt-6">
          <h2 className="text-xl font-semibold mb-2">Tickers sin fecha de resultados:</h2>
          <ul className="list-disc list-inside">
            {missingEarnings.map(ticker => <li key={ticker}>{ticker}</li>)}
          </ul>
        </div>
      )}

      <div className="mt-10">
        <h2 className="text-2xl font-semibold mb-6 text-gray-800">Dividend Information</h2>
        <div className="overflow-x-auto rounded-lg shadow-md">
          <table className="min-w-full divide-y divide-gray-200 bg-white">
            <thead className="bg-gray-100">
              <tr>
                <th className="px-6 py-3 text-left text-sm font-medium text-gray-700 uppercase tracking-wider">Ticker</th>
                <th className="px-6 py-3 text-left text-sm font-medium text-gray-700 uppercase tracking-wider">Ex-Dividend Date</th>
                <th className="px-6 py-3 text-left text-sm font-medium text-gray-700 uppercase tracking-wider">Payment Date</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-gray-200">
              {dividends.map((item) => (
                <tr key={item.ticker} className="hover:bg-gray-50 transition">
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900 font-medium">{item.ticker}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-700">{item.ex_date || '—'}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-700">{item.pay_date || '—'}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {missingDividends.length > 0 && (
        <div className="mt-6">
          <h2 className="text-xl font-semibold mb-2">Tickers sin información de dividendos:</h2>
          <ul className="list-disc list-inside">
            {missingDividends.map(ticker => <li key={ticker}>{ticker}</li>)}
          </ul>
        </div>
      )}
    </div>
  );
}
