import React, { useEffect, useState } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import axios from 'axios';

export default function App() {
  const [events, setEvents] = useState([]);
  // const tickers = ['AAPL', 'MSFT', 'GOOGL', 'NVDA', 'TSLA'];

  useEffect(() => {
    const params = { tickers: tickers.join(',') };
    axios.get('http://localhost:5000/api/earnings', { params })
      .then(resp => {
        const ev = resp.data.map(item => ({
          title: item.ticker,
          date: item.date
        }));
        setEvents(ev);
      });
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Earnings Release Calendar</h1>
      <FullCalendar
        plugins={[dayGridPlugin]}
        initialView="dayGridMonth"
        events={events}
        height="auto"
      />
    </div>
  );
}
